const { splitmessage, toUglyMessage } = require("./index");

test("Der GG Test", () => {
  expect(toUglyMessage(splitmessage("GGGGGGGG"))).toBe("§8G");
});

test("testen splitmessage", () => {
  var input = "BlaaaaaaaaaaaBlubbbbbb";
  var result = JSON.stringify([
    { char: "B", count: 1 },
    { char: "l", count: 1 },
    { char: "a", count: 11 },
    { char: "B", count: 1 },
    { char: "l", count: 1 },
    { char: "u", count: 1 },
    { char: "b", count: 6 },
  ]);
  var returnvalue = splitmessage(input);
  expect(returnvalue).toBe(result);
});

test("Test to get a Ugly Message", () => {
  var input = JSON.stringify([
    { char: "B", count: 1 },
    { char: "l", count: 1 },
    { char: "a", count: 11 },
    { char: "B", count: 1 },
    { char: "l", count: 1 },
    { char: "u", count: 1 },
    { char: "b", count: 6 },
  ]);
  var result = "Bl§11aBlu§6b";
  var rerunvalue = toUglyMessage(input);
  expect(rerunvalue).toBe(result);
});

test("Try message to JSON and then to a Ugly Message", () => {
  var message = "hhhjjjjfffffddddddghzzzzzzzttttrrrrrrrfffflllllllkkkkkkk";
  var result = "hhh§4j§5f§6dgh§7z§4t§7r§4f§7l§7k";

  var returnvalue = toUglyMessage(splitmessage(message));

  expect(returnvalue).toBe(result);
});

test("test 1", () => {
  expect(
    toUglyMessage(
      splitmessage(
        "QQQQRRRRRRTTTTTTTTTTLLLLLLLLLLLMNNNVVVVVVVVVVVAAAAAAAAAAAAA"
      )
    )
  ).toBe("§4Q§6R§10T§11LMNNN§11V§13A");
  expect(toUglyMessage(splitmessage("ABBCCCDDDDEEEEEDDDDCCCBBA"))).toBe(
    "ABBCCC§4D§5E§4DCCCBBA"
  );
  expect(toUglyMessage(splitmessage("A12399999990Z"))).toBe("A123§790Z");
  expect(toUglyMessage(splitmessage("aB12...????????!!!Z"))).toBe(
    "aB12...§8?!!!Z"
  );
  expect(toUglyMessage(splitmessage("1+2-4+55555+++++9+B"))).toBe(
    "1+2-4+§55§5+9+B"
  );
  expect(toUglyMessage(splitmessage("GGGG"))).toBe("§4G");
  expect(
    toUglyMessage(
      splitmessage(
        "Rinderkennzeichnungsfleischetikettierungsüberwachungsaufgabenübertragungsgesetz"
      )
    )
  ).toBe(
    "Rinderkennzeichnungsfleischetikettierungsüberwachungsaufgabenübertragungsgesetz"
  );
  expect(toUglyMessage(splitmessage("GGGGG"))).toBe("§5G");
  expect(toUglyMessage(splitmessage("-11123345556"))).toBe("-11123345556");
  expect(toUglyMessage(splitmessage("SSSSSSJJJJJJJHHHHRRRRZZZZEUJJ"))).toBe(
    "§6S§7J§4H§4R§4ZEUJJ"
  );
  expect(toUglyMessage(splitmessage("string"))).toBe("string");
  expect(toUglyMessage(splitmessage("string 2"))).toBe("string 2");
  expect(
    toUglyMessage(
      splitmessage(
        "Pretttttyniiiiiceeeeeeveeerythhhhhhhiiinggggyyyouuuuugotthereeeeeeeeeeitwouldbeasssssshhhhhhaaaaaammmmmmmeeeeeeeifsomethinghappppppppenedtoit"
      )
    )
  ).toBe(
    "Pre§5tyn§5ic§6eveeeryt§7hiiin§4gyyyo§5ugotther§10eitwouldbea§6s§6h§6a§7m§7eifsomethingha§8penedtoit"
  );
  expect(
    toUglyMessage(
      splitmessage(
        ""
      )
    )
  ).toBe(
    ""
  );
  expect(
    toUglyMessage(
      splitmessage(
        "LLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLOLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLLL"
      )
    )
  ).toBe("§50LO§50L");
});
